import React, { useState } from 'react';
import axios from 'axios';
import CountryForm from './CountryForm';
import CountryInfo from './CountryInfo';

const App = () => {
  const [countryData, setCountryData] = useState(null);

  const fetchCountryData = async (country: string) => {
    const response = await axios.get(`https://restcountries.eu/rest/v3.1/name/${country}`);
    console.log(response);
    setCountryData(response.data[0]);
    console.log(response.data[0]);
  };

  return (
    <div>
      {!countryData && <CountryForm onSubmit={fetchCountryData} />}
      {countryData && <CountryInfo data={countryData} />}
    </div>
  );
};

export default App;
