import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

interface Country {
  name?: {
    common?: string;
    official?: string;
    nativeName?: {
      eng?: {
        common?: string;
        official?: string;
      };
      hin?: {
        common?: string;
        official?: string;
      };
      tam?: {
        common?: string;
        official?: string;
      };
      // Add more languages as needed
    };
  };
  capital?: string[];
  population?: number;
  latlng?: number[];
  flags?: {
    png?: string;
    svg?: string;
    alt?: string;
  };
}

const CountryInfo = () => {
  const { name } = useParams();
  const [countryData, setCountryData] = useState<Country>({});
  const [weatherData, setWeatherData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCountryData = async () => {
      try {
        const response = await fetch(`https://restcountries.com/v3.1/name/${name}?fullText=true`);
        const data = await response.json();
        setCountryData(data[0]);

        if (!response.ok) {
          alert("Enter Correct Country Name");
          navigate(`/`);
        }
      } catch (error) {
        console.error("Error fetching country data:", error);
      }
    };

    fetchCountryData();
  }, [name, navigate]);

  const fetchWeatherData = async () => {
    try {
      // Replace 'YOUR_API_KEY' with your actual weather API key
    //   const response = await axios.get(`https://api.weatherapi.com/v1/current.json?key=YOUR_API_KEY&q=${countryData.capital?.[0]}`);
    //   setWeatherData(response.data);
    } catch (error) {
      console.error("Error fetching weather data:", error);
    }
  };

  useEffect(() => {
    console.log("countryData updated:", countryData);
  }, [countryData]);

  if (!countryData.name) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>
        Welcome to the Country Data
        <Link to="/">Home</Link>
      </h1>

      <p>
        <strong>Name:</strong> {countryData.name.common}
      </p>
      <p>
        <strong>Capital:</strong> {countryData.capital?.[0]}
      </p>
      <p>
        <strong>Population:</strong> {countryData.population}
      </p>
      <p>
        <strong>Latitude/Longitude:</strong> {countryData.latlng?.join(', ')}
      </p>
      <p>
        <strong>Flag:</strong> <img src={countryData.flags?.png} alt={`Flag of ${countryData.name.common}`} />
      </p>

      <button onClick={fetchWeatherData}>Capital Weather</button>

      {/* Render weatherData if available */}
      {weatherData && <div>Weather: {weatherData.current.condition.text}</div>}
    </div>
  );
};

export default CountryInfo;
