import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const CountryInfo = () => {
  const { name } = useParams();
  const [countryData, setCountryData] = useState<Country>({});
  const [weatherData, setWeatherData] = useState({});
  const navigate = useNavigate();

  interface Country {
    name?: {
      common?: string;
      official?: string;
    };
    capital?: string[];
    population?: number;
    latlng?: number[];
    flags?: {
      png?: string;
      svg?: string;
      alt?: string;
    };
  }

  useEffect(() => {
    const fetchCountryData = async () => {
        try {
          const response = await fetch(`https://restcountries.com/v3.1/name/${name}?fullText=true`);
          const data = await response.json();
          setCountryData(data[0]);
         // console.log("country data after seting data ", countryData)
          if (!response.ok) {
            alert("Enter Correct Country Name");
            navigate(`/`);
          }
        } catch (error) {
          console.error("Error fetching country data:", error);
        }
      };      
    fetchCountryData();
  }, [name]);

  const fetchWeatherData = async () => {
    try {
     // const response = await axios.get(`https://api.weatherapi.com/v1/current.json?key=YOUR_API_KEY&q=${countryData.capital?.[0]}`);
    //  const response= await axios.get(`https://api.weatherapi.com/v1/current.json?key=YOUR_API_KEY&q=${countryData.capital?.[0]}`);
     //    https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid={API key}
    //  latlng
    //  setWeatherData(response.data);
    } catch (error) {
      console.error("Error fetching weather data:", error);
    }
  };


  useEffect(() => {
    console.log("countryData updated:", countryData);
    console.log("COuntry name",countryData.name?.common )
  }, [countryData]);
  

  if (!countryData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
        <h1> Welcome to thec Country Data

        <Link to="/" >            Home          </Link>

        </h1>
    
       
      <h1>{countryData.name?.common}</h1>
      <h1>{countryData.name?.official}</h1>
      <p>Capital: {countryData.capital}</p>
      <p>Population: {countryData.population}</p>
      {/* <p>Lat/Lng: {countryData.latlng.join(', ')}</p> */}
      <img src={countryData.flags?.png} alt={`Flag of ${countryData.name}`} />
      <button onClick={fetchWeatherData}>Capital Weather</button>
   
       {/* {weatherData && <div>Weather: {weatherData.current.condition.text}</div>} 
           */}
{/* 
      Temparature and Windspeed */}
    </div>
  );
};

export default CountryInfo;
