import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CountryInfo = () => {
  const { name } = useParams();
  const [countryData, setCountryData] = useState(null);
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    const fetchCountryData = async () => {
      const response = await axios.get(`https://restcountries.com/v3.1/name/${name}`);
      console.log("Datat inside the use effect call "+ response)
      setCountryData(response.data[0]);

      console.log("Datat inside the use effect call "+ response.data[0])
    };
    fetchCountryData();
  }, [name]);

  const fetchWeatherData = async () => {
    // Replace with your weather API URL
    const response = await axios.get(`https://api.weatherapi.com/v1/current.json?key=YOUR_API_KEY&q=${countryData.capital}`);
    console.log("Datat inside the fetcg api "+ response)
    setWeatherData(response.data);
    console.log("Datat inside the fetcg api "+ response.data[0])

  };

  if (!countryData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
        <h1> Welcome to thec Country Data</h1>
        console.log("Country data"+ {countryData})

