import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Paper from "@mui/material/Paper";
import { makeStyles } from "@mui/styles";



const useStyles = makeStyles(() => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundImage: "url('https://giphy.com/embed/l3V0p1BV7BUlppblm')", // Replace with your globe image
    // <iframe src="https://giphy.com/embed/l3V0p1BV7BUlppblm" width="320" height="480" frameBorder="0" class="giphy-embed" allowFullScreen></iframe><p><a href="https://giphy.com/gifs/cinemagraph-altasensibilidad-hand-lover-l3V0p1BV7BUlppblm">via GIPHY</a></p>
   // backgroundSize: "cover",
  },
  formContainer: {
    padding: 4,
   // backgroundColor: "rgba(255, 255, 255, 0.7)", // Adjust the background color opacity
   
    //borderRadius: theme.spacing(2),
  },
}));

const CountryForm = () => {
  const [country, setCountry] = useState("");
  const navigate = useNavigate();
  const classes = useStyles();

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    navigate(`/country/${country}`);
  };

  return (
    <Container className={classes.root}>
      <Paper elevation={3} className={classes.formContainer}>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Enter country"
            variant="outlined"
            value={country}
            onChange={(e) => setCountry(e.target.value)}
            fullWidth
            required
          />
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Submit
          </Button>
        </form>
      </Paper>
    </Container>
  );
};

export default CountryForm;
