import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import Button from "@mui/material/Button";
import { makeStyles } from "@mui/styles";
import TextField from "@mui/material/TextField";
import { useNavigate } from "react-router-dom";

const backgroundImg =
  "https://images.unsplash.com/photo-1610892415063-d89a504ce049";

const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundImage: `url(${backgroundImg})`,
    backgroundSize: "cover",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    width: "500px",
    padding: 4,
    backgroundColor: "rgba(255, 255, 255, 0.8)", // Adding a semi-transparent white background
    borderRadius: "10px",
  },
  input: {
    marginBottom: 2,
    width: "100%",
  },
  button: {
    borderRadius: "20px",
    marginTop: 2,
    width: "100%",
  },
  infoContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: 4,
    backgroundColor: "rgba(255, 255, 255, 0.8)",
    borderRadius: "10px",
    marginTop:2,
  },
}));

const CountryForm = () => {
  const [country, setCountry] = useState("");
  const navigate = useNavigate();
  const classes = useStyles();

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    navigate(`/country/${country}`);
  };

  return (
    <div className={classes.root}>
      <form className={classes.form} onSubmit={handleSubmit}>
        <TextField
          label="Enter country"
          variant="outlined"
          value={country}
          onChange={(e) => setCountry(e.target.value)}
          className={classes.input}
          required
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
        >
          Submit
        </Button>
      </form>
    </div>
  );
};

interface Country {
  name?: {
    common?: string;
    official?: string;
  };
  capital?: string[];
  population?: number;
  latlng?: number[];
  flags?: {
    png?: string;
    svg?: string;
    alt?: string;
  };
}

const CountryInfo = () => {
  const { name } = useParams();
  const [countryData, setCountryData] = useState<Country>({});
  const [weatherData, setWeatherData] = useState<any>({});
  const navigate = useNavigate();
  const API_KEY = "e3736c59809579a0c2080ae39ebc7d67";
  const classes = useStyles();

  useEffect(() => {
    const fetchCountryData = async () => {
      try {
        const response = await fetch(
          `https://restcountries.com/v3.1/name/${name}?fullText=true`
        );
        const data = await response.json();
        setCountryData(data[0]);
        if (!response.ok) {
          alert(
            "Enter Correct Country Name OR Error Occurred While Fetching Data"
          );
          navigate(`/`);
        }
      } catch (error) {
        console.error("Error fetching country data:", error);
      }
    };

    fetchCountryData();
  }, [name, navigate]);

  const fetchWeatherData = async () => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?lat=${countryData.latlng?.[0]}&lon=${countryData.latlng?.[1]}&appid=${API_KEY}`
      );
      setWeatherData(response.data);
    } catch (error) {
      console.error("Error fetching weather data:", error);
    }
  };

  useEffect(() => {
    if (countryData.latlng) {
      fetchWeatherData();
    }
  }, [countryData]);

  if (!countryData.name) {
    return <div>Loading...</div>;
  }

  const renderWeatherData = () => {
    return (
      <div className={classes.infoContainer}>
        <h2>Weather Information:</h2>
        <p>
          <strong>Temperature:</strong>{" "}
          {Math.round(weatherData.main?.temp - 273.15)} Celsius
        </p>
        <p>
          <strong>Wind Speed:</strong> {weatherData.wind?.speed} meter/sec
        </p>
      </div>
    );
  };

  return (
    <div className={classes.root}>
      <div className={classes.infoContainer}>
        <h1>
          Welcome to the Country Data
          <Link to="/">Home</Link>
        </h1>

        <p>
          <strong>Name:</strong> {countryData.name.common}
        </p>
        <p>
          <strong>Capital:</strong> {countryData.capital?.[0]}
        </p>
        <p>
          <strong>Population:</strong> {countryData.population}
        </p>
        <p>
          <strong>Latitude/Longitude:</strong> {countryData.latlng?.join(", ")}
        </p>
        <p>
          <strong>Flag:</strong>{" "}
          <img
            src={countryData.flags?.png}
            alt={`Flag of ${countryData.name.common}`}
          />
        </p>
        <Button
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={fetchWeatherData}
        >
          Capital Weather
        </Button>
        {weatherData && renderWeatherData()}
      </div>
    </div>
  );
};

export { CountryForm, CountryInfo };
