import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { makeStyles } from "@mui/styles";

const backgroundImg='https://images.unsplash.com/photo-1610892415063-d89a504ce049'
const useStyles = makeStyles(() => ({
  form: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: "50%",
    marginLeft: "50%",

    // backgroundImage: "linear-gradient(to top, #f3e7e9 0%, #e3eeff 99%, #e3eeff 100%)",
    backgroundImage: `url(${backgroundImg})`,
    backgroundSize: "cover", // Adjust as needed
    minHeight: "60vh",
    width: "200px", // Ensure the form covers the entire viewport height
    justifyContent: "center",
    padding: 4, //theme.spacing(4),
  },
  input: {
    marginBottom: 2,
    width: "300px",
  },
  button: {
    borderRadius: "20px", // Adjust the border radius as needed
  },
}));

const CountryForm = () => {
  const [country, setCountry] = useState("");
  const navigate = useNavigate();
  const classes = useStyles();

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    navigate(`/country/${country}`);
  };

  return (
    <form className={classes.form} onSubmit={handleSubmit}>
      <TextField
        label="Enter country"
        variant="outlined"
        value={country}
        onChange={(e) => setCountry(e.target.value)}
        className={classes.input}
        required
      />
      <Button
        type="submit"
        variant="contained"
        color="primary"
        className={classes.button}
      >
        Submit
      </Button>
    </form>
  );
};

export default CountryForm;






























































